public class Var1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 변수 선언 공식
		// 데이터타입 변수명
	
		byte num; num = 10; // = 대입연산자
		System.out.println(num);
		num = 35;
		System.out.println(num);
//		num = 3000000;
		
		int num2;
		num2= 4000000;
		
		long num3;
		num3 = 4000000000L;
		
		byte num4 = 100+10;
//		num4 = num4 + 10;
		long num5 = 100L;
		System.out.println(num2 + num3 + num4 + num5);
		
//		선언
		int num6;
		num6=10; // 초기화
		System.out.print(num6);
	}
}